mapper.select:
SELECT * FROM users
WHERE name = #{name}
  AND email = #{email}
  AND phone = #{phone}
  AND status = 'ACTIVE'

SELECT * FROM users
WHERE username = #{username}
  AND email = #{email}
  AND status = 'ACTIVE'

mapper.update:
UPDATE users
SET total_point = total_point + #{amount},
    point_balance = point_balance + #{amount},
    exp = exp + #{amount}
WHERE id = #{userId}

UPDATE users
SET point_balance = point_balance - #{amount}
WHERE id = #{userId} AND point_balance >= #{amount}

UPDATE users u
    JOIN study_plans p ON u.id = p.user_id
    SET u.total_point = u.total_point + #{point},
        u.point_balance = u.point_balance + #{point},
        u.exp = u.exp + #{point}
WHERE p.id = #{planId}
